/**
 * highlight.js基本功能实现
 * 当CDN无法访问时的备用方案
 */
var hljs = {
  /**
   * 高亮显示元素
   * @param {HTMLElement} block - 要高亮的元素
   */
  highlightElement: function(block) {
    // 提供一个简单的高亮样式
    if (block) {
      block.style.backgroundColor = '#f6f8fa';
      block.style.padding = '10px';
      block.style.borderRadius = '4px';
      block.style.border = '1px solid #ddd';
      block.style.fontFamily = 'monospace';
    }
    return block;
  },
  
  /**
   * 获取特定语言的高亮配置
   * @param {string} lang - 语言名称
   * @returns {Object|null} - 语言配置对象或null
   */
  getLanguage: function(lang) {
    // 返回一个简单的支持对象表示支持该语言
    return { name: lang };
  },
  
  /**
   * 根据语言高亮代码
   * @param {string} code - 要高亮的代码
   * @param {Object} options - 高亮选项
   * @returns {Object} - 高亮结果
   */
  highlight: function(code, options) {
    return {
      value: code,
      language: options && options.language ? options.language : 'text'
    };
  },
  
  /**
   * 自动检测语言并高亮代码
   * @param {string} code - 要高亮的代码
   * @returns {Object} - 高亮结果
   */
  highlightAuto: function(code) {
    return {
      value: code,
      language: 'text'
    };
  }
};

console.log("已加载highlight.js本地备用版本");
window.hljs = hljs; 